import React from "react";

type Props = {
    activeCity: string;
    onCityClick : Function;
};

type State = {
    activeCity: string;
};

class CityNav extends React.Component<Props, State> {
    constructor(props : Props) {
        super(props);
        this.state = {activeCity: props.activeCity};
    }

    render() {

        return (
            <nav className="city-nav">
                <ul className="city-nav-list">
                    <li>
                        <div className={`city-nav-item ${this.state.activeCity === "St. John's" ? "active" : ""}`} 
                             onClick={() => {this.props.changeCity("St. John's")}}>
                            St. John's
                        </div>
                    </li>
                    <li>
                        <div className={`city-nav-item ${this.state.activeCity === "Tokyo" ? "active" : ""}`} 
                             onClick={() => {this.props.changeCity("Tokyo")}}>
                            Tokyo
                        </div>
                    </li>
                    <li>
                        <div className={`city-nav-item ${this.state.activeCity === "Madrid" ? "active" : ""}`} 
                             onClick={() => {this.props.changeCity("Madrid")}}>
                            Madrid
                        </div>
                    </li>
                </ul>
            </nav>
        )
    }
}

export default CityNav;
