import React from "react";

type Props = {
    isLarge : boolean;
    temp : number;
    weather : string;
    placeholder : boolean;
};

type State = {

};

class WeatherCard extends React.Component<Props, State> {
    constructor(props : Props) {
        super(props);
        this.state = {};
    }

    render() {
        return (
            <div className={`weather-card ${this.props.isLarge ? "large" : ""}`}>
                {this.props.placeholder ? <div className="card-title">Today</div> :
                    <div className="card-title">Loading...</div>
                }
                <div className="card-content">
                    <i className="fa-solid fa-cloud"></i>
                    <div className="card-content-info">
                        <div className="card-content-info-temp">{`${this.props.temp}°`}</div>

                        {this.props.placeholder ? <div className="card-content-info-weather">Loading</div> :
                            (this.props.isLarge ? <div className="card-content-info-weather">{this.props.weather}</div> : 
                            "")
                        }
                    </div>
                </div>
            </div>
        )
    }
}

export default WeatherCard;
