  constructor(props : Props) {
    super(props);
    this.state = {activeCity: "St. John's"};
  }

  changeCity(cityName : string) {
      this.setState((state) => ({
          activeCity: cityName,
      }));
  }

  render() {
    return (
      <div className="App">
        <header className="App-header">
          <CityNav activeCity={this.state.activeCity} onCityClick={this.changeCity.bind(this)}></CityNav>
        </header>
        <main className="App-content">
          <WeatherPanel></WeatherPanel>
        </main>
        <footer className='App-footer'>

        </footer>
      </div>
    );
  }
  }

export default App;
