import React from "react";

type Props = {
    activeCity: string;
};

type State = {
    activeCity: string;
};

class CityNav extends React.Component<Props, State> {
    constructor(props : Props) {
        super(props);
        this.state = {activeCity: props.activeCity};
    }

    changeCity(cityName : string) {
        this.setState((state) => ({
            activeCity: cityName,
        }));
    }

    render() {

        return (
            <nav className="city-nav">
                <ul className="city-nav-list">
                    <li>
                        <div className={`city-nav-item ${this.state.activeCity === "St. John's" ? "active" : ""}`}>St. John's</div>
                    </li>
                    <li>
                        <div className={`city-nav-item ${this.state.activeCity === "Tokyo" ? "active" : ""}`}>Tokyo</div>
                    </li>
                    <li>
                        <div className={`city-nav-item ${this.state.activeCity === "Madrid" ? "active" : ""}`}>Madrid</div>
                    </li>
                </ul>
            </nav>
        )
    }
}

export default CityNav;
