import React from "react";
import WeatherCard from "./WeatherCard";
import { WeatherData } from "./weatherData";

type Props = {
    loaded : boolean;
    weatherData: Array<WeatherData> | undefined;
};

type State = {

};

class WeatherPanel extends React.Component<Props, State> {
    constructor(props : Props) {
        super(props);
        this.state = {};
    }

    render() {
        console.log(this.props.weatherData)
        let todayCard = <WeatherCard isLarge={true} temp={0} weather={"Loading"} placeholder={true}></WeatherCard>
        if(this.props.loaded && this.props.weatherData) {
            let todayData = this.props.weatherData[0];

            todayCard = <WeatherCard isLarge={true} temp={todayData.temp} weather={todayData.weatherDesc} placeholder={false}></WeatherCard>
            let upcomingData = this.props.weatherData.slice(1);
        }

        return (
            <div className="weather-panel">
                <div className="weather-panel-content">
                    <div className="current-weather-section">
                        {todayCard}
                    </div>
                    <div className="upcoming-weather-section">
                        <WeatherCard isLarge={false} temp={14} weather={"Clouds"} placeholder={false}></WeatherCard>
                        <WeatherCard isLarge={false} temp={18} weather={"Rain"} placeholder={false}></WeatherCard>
                        <WeatherCard isLarge={false} temp={21} weather={"Scattered Cloud"} placeholder={false}></WeatherCard>
                        <WeatherCard isLarge={false} temp={26} weather={"Snow"} placeholder={false}></WeatherCard>
                    </div>
                </div>
            </div>
        )
    }
}

export default WeatherPanel;
