import React from 'react';
import CityNav from './CityNav';
import WeatherPanel from './WeatherPanel';
import './App.css';
import { render } from '@testing-library/react';

type Props = {

};

type State = {
  cityList : Array<string>;
  cityWeatherId : Object;
  activeCity : string;
};

class App extends React.Component<Props, State>{
  constructor(props : Props) {
    super(props);

    this.state = {
      cityList: ["St. John's", "Tokyo", "Madrid"],
      cityWeatherId: {
        "St. John's": 6324733,
        "Tokyo": 1850147,
        "Madrid": 6359304,
      },
      activeCity: "St. John's",
    };
  }

  changeCity(activeCity : string) {
      this.setState((state) => ({
          activeCity
      }));
  }

  render() {
    return (
      <div className="App">
        <header className="App-header">
          <CityNav cityList={this.state.cityList} activeCity={this.state.activeCity} onCityClick={this.changeCity.bind(this)}></CityNav>
        </header>
        <main className="App-content">
          <WeatherPanel></WeatherPanel>
        </main>
        <footer className='App-footer'>

        </footer>
      </div>
    );
  }
  }

export default App;
